const item = $input.first();
const webhookData = item.json;

// CRITICAL: Validate required workspace data
const userId = webhookData.body?.supabase_user_id || webhookData.supabase_user_id;
const workspaceId = webhookData.body?.workspace_id || webhookData.workspace_id;
const source = webhookData.body?.source || webhookData.source;
const userEmail = webhookData.body?.user_email || webhookData.user_email;

console.log('ðŸ”’ Security validation:');
console.log('- User ID:', userId);
console.log('- Workspace ID:', workspaceId);
console.log('- Source:', source);
console.log('- Email:', userEmail);

// SECURITY: Reject if missing critical data
if (!userId || !workspaceId) {
  throw new Error('SECURITY VIOLATION: Missing user_id or workspace_id in webhook payload');
}

// Validate UUID format
const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(userId) || !uuidRegex.test(workspaceId)) {
  throw new Error('SECURITY VIOLATION: Invalid UUID format for user_id or workspace_id');
}

// Check file upload exists
if (!item.binary || !item.binary.file) {
  throw new Error('No file found in upload');
}

const fileData = item.binary.file;
const fileName = fileData.fileName || 'receipt';
const mimeType = fileData.mimeType || '';
const fileExt = fileName.split('.').pop().toLowerCase();

console.log(`ðŸ“„ File uploaded: ${fileName} (${mimeType})`);

// Define file categories
const imageFormats = ['jpg', 'jpeg', 'png', 'gif', 'webp', 'bmp'];
const needsConversion = ['pdf', 'doc', 'docx', 'tiff', 'tif', 'heic', 'heif'];
const unsupportedFormats = ['txt', 'csv', 'xlsx', 'mp4', 'avi', 'zip'];

// Determine file type and action needed
let fileCategory = 'unknown';
let actionRequired = 'unknown';

if (imageFormats.includes(fileExt) || mimeType.startsWith('image/')) {
  fileCategory = 'image';
  actionRequired = 'direct_ocr';
} else if (needsConversion.includes(fileExt) || mimeType === 'application/pdf') {
  fileCategory = 'document';
  actionRequired = 'convert_then_ocr';
} else if (unsupportedFormats.includes(fileExt)) {
  throw new Error(`âŒ Unsupported file type: ${fileExt}. Please upload images (JPG, PNG) or documents (PDF).`);
} else {
  fileCategory = 'unknown';
  actionRequired = 'convert_then_ocr';
}

console.log(`ðŸ” File type: ${fileCategory}, Action: ${actionRequired}`);

return {
  json: {
    fileName,
    fileExtension: fileExt,
    mimeType,
    fileCategory,
    actionRequired,
    originalFile: true,
    // SECURITY: Pass through validated workspace data
    supabase_user_id: userId,
    workspace_id: workspaceId,
    upload_source: source,
    user_email: userEmail,
    upload_timestamp: new Date().toISOString()
  },
  binary: {
    file: fileData
  }
};

const securityData = $('Validate Workspace Security').first().json;
const accessData = $('Validate Workspace Access').first().json;

console.log('ðŸ›¡ï¸ Access validation result:', accessData);

// SECURITY: Reject if user doesn't have workspace access
if (!accessData.has_access || accessData.has_access !== true) {
  throw new Error(`SECURITY VIOLATION: User ${securityData.supabase_user_id} does not have access to workspace ${securityData.workspace_id}`);
}

console.log('âœ… Workspace access validated - proceeding with OCR');
console.log('- User role:', accessData.user_role);
console.log('- Workspace name:', accessData.workspace_name);

// Continue with file processing logic
const actionRequired = securityData.actionRequired;

return {
  json: {
    ...securityData,
    workspace_validated: true,
    user_role: accessData.user_role,
    workspace_name: accessData.workspace_name
  },
  binary: securityData.binary || {}
};

const item = $input.first();

if (!item.binary || !item.binary.file) {
  throw new Error('No file found');
}

const fileData = item.binary.file;
const fileName = fileData.fileName || 'receipt.jpg';
let mimeType = fileData.mimeType || '';

// Ensure proper image MIME type
const fileExt = fileName.split('.').pop().toLowerCase();
if (!mimeType.startsWith('image/')) {
  if (fileExt === 'jpg' || fileExt === 'jpeg') {
    mimeType = 'image/jpeg';
  } else if (fileExt === 'png') {
    mimeType = 'image/png';
  } else {
    mimeType = 'image/jpeg';
  }
}

console.log(`âœ… Image ready for direct OCR: ${fileName} (${mimeType})`);

// Pass through all security data
const securityData = item.json;

return {
  json: {
    ...securityData,
    fileName,
    fileExtension: fileExt,
    mimeType,
    isValidImage: true,
    processingId: `direct_${Date.now()}`,
    wasConverted: false
  },
  binary: {
    data: {
      data: fileData.data,
      mimeType: mimeType,
      fileName: fileName
    }
  }
};

// Parse OpenAI image analysis response
const items = $input.all();

return items.map(item => {
  // Get security data from previous nodes
  const securityData = item.json;

  // OpenAI image analysis returns data in different fields
  let rawResponse = '';

  // Try different possible response fields from OpenAI node
  if (item.json.choices && item.json.choices[0] && item.json.choices[0].message) {
    rawResponse = item.json.choices[0].message.content;
  } else if (item.json.content) {
    rawResponse = item.json.content;
  } else if (item.json.response) {
    rawResponse = item.json.response;
  } else if (item.json.text) {
    rawResponse = item.json.text;
  } else if (item.json.output) {
    rawResponse = item.json.output;
  } else {
    console.log('Available response keys:', Object.keys(item.json));
    console.log('Full response:', JSON.stringify(item.json).substring(0, 500));
    throw new Error('No response found. Check debug logs above.');
  }

  if (!rawResponse) {
    throw new Error('Empty response from OpenAI');
  }

  console.log('Raw response (first 200 chars):', rawResponse.substring(0, 200));

  // Clean the response - remove code blocks if present
  let cleanResponse = rawResponse
    .replace(/^```json\s*/i, '')
    .replace(/^```\s*/i, '')
    .replace(/\s*```$/g, '')
    .trim();

  let parsed;
  try {
    parsed = JSON.parse(cleanResponse);
  } catch (error) {
    console.error('Failed to parse JSON. Raw response:', cleanResponse.substring(0, 500));
    throw new Error(`JSON parse failed: ${error.message}`);
  }

  // Validate and normalize the extracted data
  const result = {
    // SECURITY: Include workspace data in result
    workspace_id: securityData.workspace_id,
    supabase_user_id: securityData.supabase_user_id,
    user_role: securityData.user_role,
    workspace_name: securityData.workspace_name,

    // OCR Results
    success: true,
    merchant_name: parsed.merchant_name || '',
    location: parsed.location || '',
    total_amount: parseFloat(parsed.total_amount) || 0,
    currency: parsed.currency || 'USD',
    transaction_date: parsed.transaction_date || '',
    transaction_time: parsed.transaction_time || '',
    line_items: Array.isArray(parsed.line_items) ? parsed.line_items : [],
    tax_amount: parseFloat(parsed.tax_amount) || 0,
    tip_amount: parseFloat(parsed.tip_amount) || 0,
    payment_method: parsed.payment_method || '',
    receipt_number: parsed.receipt_number || '',
    confidence_score: parseFloat(parsed.confidence_score) || 0,
    extraction_notes: parsed.extraction_notes || '',
    processed_at: new Date().toISOString(),

    // Debug info
    _debug: {
      originalResponseKeys: Object.keys(item.json),
      rawResponseLength: rawResponse.length,
      workspaceValidated: true
    }
  };

  console.log(`âœ… Extracted: ${result.merchant_name} - $${result.total_amount} (confidence: ${result.confidence_score})`);
  console.log(`ðŸ¢ Workspace: ${result.workspace_name} (${result.workspace_id})`);

  return { json: result };
});

const item = $input.first();
const storageResponse = item.json;
const securityData = item.json; // Security data should be passed through

console.log('ðŸ“ Storage response for workspace:', securityData.workspace_id);

// Check if storage was successful
if (!storageResponse || (!storageResponse.Key && !storageResponse.name)) {
  console.warn('Storage upload may have failed - no Key or name found');
  return {
    json: {
      ...securityData,
      storage_success: false,
      storage_url: null,
      storage_key: null,
      error: 'No storage key returned'
    }
  };
}

// Extract the storage key/path - Supabase Storage API returns different formats
const storageKey = storageResponse.Key || storageResponse.name || storageResponse.path;

// Build the workspace-scoped public URL
const publicUrl = storageKey ? 
  `https://lrwvooucggciazmzxqlb.supabase.co/storage/v1/object/public/${storageKey}` : 
  null;

console.log('ðŸ“ Storage key:', storageKey);
console.log('ðŸ”— Public URL:', publicUrl);

return {
  json: {
    ...securityData,
    storage_success: true,
    storage_url: publicUrl,
    storage_key: storageKey,
    original_response: storageResponse
  }
};

const items = $input.all();

// Find OCR data and storage data from the merged inputs
let ocrData = null;
let storageData = null;

items.forEach(item => {
  if (item.json.merchant_name !== undefined) {
    ocrData = item.json;
  }
  if (item.json.storage_success !== undefined) {
    storageData = item.json;
  }
});

if (!ocrData) {
  throw new Error('No OCR data found in merged inputs');
}

// SECURITY: Validate workspace consistency
if (ocrData.workspace_id !== storageData?.workspace_id) {
  throw new Error('SECURITY VIOLATION: Workspace ID mismatch between OCR and storage data');
}

console.log('ðŸ”„ Processing merged data for workspace:', ocrData.workspace_id);
console.log('- Merchant:', ocrData.merchant_name);
console.log('- Amount:', ocrData.total_amount);
console.log('- Storage success:', storageData?.storage_success);

// Combine both data sets with workspace validation
return {
  json: {
    ...ocrData,
    ...storageData,
    workspace_validated: true,
    ready_for_transaction_creation: true
  }
};

const combinedData = $input.first().json;

// SECURITY: Double-check workspace_id exists
if (!combinedData.workspace_id) {
  throw new Error('SECURITY VIOLATION: No workspace_id in transaction preparation');
}

console.log('ðŸ’° Preparing transaction for workspace:', combinedData.workspace_id);
console.log('- User:', combinedData.supabase_user_id);
console.log('- Merchant:', combinedData.merchant_name);
console.log('- Amount:', combinedData.total_amount);

// Smart categorization based on merchant name
function determineCategory(merchantName) {
  if (!merchantName) return 'Other Expenses';

  const merchant = merchantName.toLowerCase();

  // Food & Dining
  if (merchant.includes('whole foods') || merchant.includes('trader joe') || 
      merchant.includes('safeway') || merchant.includes('kroger') || 
      merchant.includes('walmart') || merchant.includes('target') ||
      merchant.includes('grocery') || merchant.includes('market')) {
    return 'Groceries';
  }

  if (merchant.includes('starbucks') || merchant.includes('dunkin') || 
      merchant.includes('coffee') || merchant.includes('cafe') || 
      merchant.includes('tea')) {
    return 'Coffee Shops';
  }

  if (merchant.includes('restaurant') || merchant.includes('grill') || 
      merchant.includes('kitchen') || merchant.includes('bistro') || 
      merchant.includes('pizza') || merchant.includes('sushi') ||
      merchant.includes('bar') || merchant.includes('diner')) {
    return 'Restaurants';
  }

  // Transportation
  if (merchant.includes('shell') || merchant.includes('chevron') || 
      merchant.includes('exxon') || merchant.includes('mobil') || 
      merchant.includes('gas') || merchant.includes('fuel')) {
    return 'Gas & Fuel';
  }

  if (merchant.includes('uber') || merchant.includes('lyft') || 
      merchant.includes('taxi') || merchant.includes('transit')) {
    return 'Transportation';
  }

  // Shopping
  if (merchant.includes('amazon') || merchant.includes('ebay') || 
      merchant.includes('etsy')) {
    return 'Online Shopping';
  }

  if (merchant.includes('mall') || merchant.includes('store') || 
      merchant.includes('shop') || merchant.includes('boutique')) {
    return 'Shopping';
  }

  // Services
  if (merchant.includes('netflix') || merchant.includes('spotify') || 
      merchant.includes('hulu') || merchant.includes('disney')) {
    return 'Subscriptions';
  }

  if (merchant.includes('verizon') || merchant.includes('at&t') || 
      merchant.includes('t-mobile') || merchant.includes('comcast')) {
    return 'Phone & Internet';
  }

  return 'Other Expenses';
}

// Format for Supabase transaction - WORKSPACE SCOPED
const transactionData = {
  workspace_id: combinedData.workspace_id, // CRITICAL: Workspace scoped
  merchant: combinedData.merchant_name || 'Unknown Merchant',
  amount: -Math.abs(combinedData.total_amount || 0), // Negative for expenses
  description: `Receipt: ${combinedData.merchant_name || 'Unknown'}`,
  category_name: determineCategory(combinedData.merchant_name),
  transaction_type: 'expense',
  status: 'completed',
  transaction_date: combinedData.transaction_date || new Date().toISOString().split('T')[0],
  currency: combinedData.currency || 'USD',
  receipt_url: combinedData.storage_url || null,
  notes: [
    combinedData.extraction_notes ? `OCR Notes: ${combinedData.extraction_notes}` : '',
    combinedData.line_items && combinedData.line_items.length > 0 ? `Items: ${JSON.stringify(combinedData.line_items)}` : '',
    combinedData.confidence_score ? `Confidence: ${(combinedData.confidence_score * 100).toFixed(1)}%` : '',
    combinedData.receipt_number ? `Receipt #: ${combinedData.receipt_number}` : '',
    combinedData.payment_method ? `Payment: ${combinedData.payment_method}` : '',
    combinedData.tax_amount ? `Tax: $${combinedData.tax_amount}` : '',
    combinedData.tip_amount ? `Tip: $${combinedData.tip_amount}` : '',
    combinedData.storage_success ? `Receipt stored: ${combinedData.storage_key}` : 'Receipt storage failed',
    `Processed by: ${combinedData.user_role} in ${combinedData.workspace_name}`
  ].filter(Boolean).join('\n'),
  tags: combinedData.confidence_score < 0.7 ? ['receipt', 'processed', 'low-confidence', 'needs-review'] : ['receipt', 'processed'],
  created_by: combinedData.supabase_user_id,
  updated_by: combinedData.supabase_user_id
};

console.log('ðŸ“Š Transaction data (workspace-scoped):');
console.log('- Workspace ID:', transactionData.workspace_id);
console.log('- Amount:', transactionData.amount);
console.log('- Category:', transactionData.category_name);

return { json: transactionData };

const transactionResult = $input.first().json;

console.log('âœ… Transaction created successfully!');
console.log('- Transaction ID:', transactionResult.id);
console.log('- Workspace ID:', transactionResult.workspace_id);
console.log('- Amount:', transactionResult.amount);
console.log('- Merchant:', transactionResult.merchant);

// Return final result with security validation confirmation
return {
  json: {
    success: true,
    transaction_id: transactionResult.id,
    workspace_id: transactionResult.workspace_id,
    merchant: transactionResult.merchant,
    amount: transactionResult.amount,
    category: transactionResult.category_name,
    receipt_url: transactionResult.receipt_url,
    processing_time: new Date().toISOString(),
    security_validated: true,
    message: `Receipt processed successfully for workspace ${transactionResult.workspace_id}`
  }
};
